import React from 'react'
import {Switch, Route} from 'react-router'

import Phones from 'containers/phones'

const Routes = (
  <Switch>
    <Route path='/' component={Phones} exact />
  </Switch>
)

const Layout = () => {
  return(
    <div className='col-md-9'>
      {Routes}
    </div>
  )
}

export default Layout